import { CommonModule } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable, map, of, switchMap } from 'rxjs';
import { OnlineStatusDirective } from '../../directives/online-status.directive';
import { Player } from '../../interfaces/player';
import { JoinPipe } from '../../pipes/join/join.pipe';
import { ApiService } from '../../services/api.service';

@Component({
  selector: 'app-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css'],
  standalone: true,
  imports: [CommonModule, JoinPipe, OnlineStatusDirective]
})
export class ProfileComponent implements OnInit {
  public player$: Observable<Player | undefined> = of(undefined);

  constructor(
    private api: ApiService,
    private route: ActivatedRoute
  ) { }

  public ngOnInit(): void {
    this.player$ = this.route.paramMap.pipe(
      map(params => params.get('id') ?? ''),
      switchMap(id => this.api.getPlayerById$(id))
    );
  }

}
<h2 *ngIf="player$ | async as player; else loading" class="my-4" [appOnlineStatus]="player.online">Profile for {{ player.id }}</h2>

<dl *ngIf="player$ | async as player">
  <dt>Name</dt>
  <dd class="lead">{{ player.name }}</dd>

  <dt>Score</dt>
  <dd class="lead">{{ player.score | percent : '1.1-1' }}</dd>

  <dt>Gems</dt>
  <dd class="lead">{{ player.gems | join }}</dd>
</dl>

<ng-template #loading>
  <p class="my-4">Loading&hellip;</p>
</ng-template>
